package lab04;

import org.apache.commons.math3.geometry.spherical.oned.ArcsSet;
import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.*;

import java.util.*;
import java.util.stream.*;
import org.junit.jupiter.api.*;
import org.junit.jupiter.params.*;
import org.junit.jupiter.params.provider.*;

class SplittingTest {

    @ParameterizedTest
    @MethodSource("splittingTest")
    void canBalance(String description, int [] nums, boolean expected){
        boolean result = Splitting.canBalance(nums);
        assertEquals(expected, result);
    }

    private static Stream<Arguments> splittingTest() {
        return Stream.of(Arguments.of("Single Null", null, false),
                Arguments.of("Equal split", new int[]{1, 1}, true),
                Arguments.of("Null", null, false),
                Arguments.of("Single", new int[]{1}, false),
                Arguments.of("Equal split", new int[]{8,2,8}, false),
                Arguments.of("Non-Equal split", new int[]{1,2}, false));
    }

}